script.sql:
CREATE TRIGGER tb_atualizacoes_before_insert BEFORE INSERT ON tb_atualizacoes FOR EACH ROW BEGIN
	SET NEW.dt_cadastro	:=	NOW();
END;

CREATE TRIGGER tb_atualizacoes_before_update BEFORE UPDATE ON tb_atualizacoes FOR EACH ROW BEGIN
	SET NEW.dt_atualizacao	:=	NOW();
END;

CREATE TRIGGER tb_tipohistorico_os_before_insert BEFORE INSERT ON tb_tipohistorico_os FOR EACH ROW BEGIN
	SET NEW.dt_cadastro	:=	NOW();
END;

CREATE TRIGGER tb_tipohistorico_os_before_update BEFORE UPDATE ON tb_tipohistorico_os FOR EACH ROW BEGIN
	SET NEW.dt_atualizacao	:=	NOW();
END;

CREATE TRIGGER tb_historicoos_before_insert BEFORE INSERT ON tb_historicoos FOR EACH ROW BEGIN
	SET NEW.dt_cadastro	:=	NOW();
END;

CREATE TRIGGER tb_historicoos_before_update BEFORE UPDATE ON tb_historicoos FOR EACH ROW BEGIN
	SET NEW.dt_atualizacao	:=	NOW();
END;

CREATE DEFINER=root@localhost TRIGGER tb_ordemserv_before_update BEFORE UPDATE ON tb_ordemserv FOR EACH ROW BEGIN
	SET NEW.dt_atualizacao	:=	NOW();
	call HistoricoAutom(2, new.id, 'Ordem de serviço atualizada');
END;

CREATE TRIGGER tb_ordemserv_before_delete BEFORE DELETE ON tb_ordemserv FOR EACH ROW BEGIN
	DELETE FROM tb_historicoos WHERE id_ordem = OLD.id;
END;
